mapper.select:
select
id, messageid, content, create_time, status, over_time, message_type, send_type, good_name, price
from miaosha_message

    
        and id = #{id}
    
    
        and messageid = #{messageid}
    
    
        and content = #{content}
    
    
        and create_time = #{createTime}
    
    
        and status = #{status}
    
    
        and over_time = #{overTime}
    
    
        and message_type = #{messageType}
    
    
        and send_type = #{sendType}
    
    
        and good_name = #{goodName}
    
    
        and price = #{price}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from miaosha_message

    
        and id = #{id}
    
    
        and messageid = #{messageid}
    
    
        and content = #{content}
    
    
        and create_time = #{createTime}
    
    
        and status = #{status}
    
    
        and over_time = #{overTime}
    
    
        and message_type = #{messageType}
    
    
        and send_type = #{sendType}
    
    
        and good_name = #{goodName}
    
    
        and price = #{price}
