mapper.select:
select
id, userid, messageid, goodid, orderid
from miaosha_message_user

    
        and id = #{id}
    
    
        and userid = #{userid}
    
    
        and messageid = #{messageid}
    
    
        and goodid = #{goodid}
    
    
        and orderid = #{orderid}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from miaosha_message_user

    
        and id = #{id}
    
    
        and userid = #{userid}
    
    
        and messageid = #{messageid}
    
    
        and goodid = #{goodid}
    
    
        and orderid = #{orderid}
